var accordion=(function(a){return{open:function(b){b.closest(".item").toggleClass("open").find(".content:first").slideToggle("fast")
},init:function(){var b=window.location.hash;
if(b!=""){b=b.replace("#","");
accordion.open($('a[name="'+b+'"]').parents())
}$(document).on("click",".c-accordion .panel",function(c){accordion.open($(this));
c.preventDefault()
})
}}
}());
$=jQuery;
$(document).ready(function(){accordion.init();
$(document).on("click","a",function(){if($(this).attr("href")!=undefined&&$(this).attr("href")!="#"&&$(this).attr("href").indexOf("#")!=-1&&!$(this).hasClass("close-recbox")){$(".open:first").find(".content:first").slideUp();
accordion.open($("a[name="+$(this).attr("href").substring($(this).attr("href").indexOf("#")+1)+"]").parents())
}})
});