$=jQuery;
$("document").ready(function(){initRoleBasedAccordion()
});
function initRoleBasedAccordion(){$(".c-tabs-home .tab-pane h2 a").click(function(){if(window.innerWidth<767){var b=$(this).parents(".tab-pane");
if(b.hasClass("active-accordion")){b.removeClass("active-accordion").css("height","49px")
}else{var a=30;
b.children(".span6").each(function(){a+=$(this).outerHeight()
});
b.addClass("active-accordion").css("height",a+"px")
}return false
}})
};